import React from "react";
import "./Search.scss";
import Template from "../components/Template";
import { useQuery } from "@apollo/client";
import { gql } from "../__generated__";

const LIST_CATS = gql(`
  query ListCats($cursor: String, $search: String) {
    cats_connection(first: 10, after: $cursor, where: {name: {_ilike: $search}}) {
      edges {
        node {
          id
          name
          owner {
            name
          }
        }
      }
      pageInfo {
        endCursor
        hasNextPage
      }
    }
  }
`);

function Search() {
  const handleSubmit = (event: React.FormEvent) => {
    event.preventDefault();
  };

  const search = "%";

  const { loading, error, data, fetchMore } = useQuery(LIST_CATS);

  const pageInfo = data?.cats_connection.pageInfo;
  const hasNext = pageInfo?.hasNextPage;
  const handleMoreResults = async () => {
    if (!hasNext) {
      return;
    }

    await fetchMore({
      variables: { cursor: pageInfo?.endCursor },
    });
  };

  if (loading) return <Template>Loading...</Template>;
  if (error) return <Template>Error : {error.message}</Template>;

  const elements = data?.cats_connection.edges.map((e) => {
    const c = e.node;
    return (
      <li key={c.id}>
        {c.name} - {c.owner.name}
      </li>
    );
  });

  return (
    <Template>
      <form onSubmit={handleSubmit}>
        <label>
          Search:
          <input type="text" name="name" />
        </label>
        <input type="submit" value="Submit" />
      </form>
      <ol>{elements}</ol>
      {hasNext && (
        <a href="#" onClick={handleMoreResults}>
          {" "}
          More results{" "}
        </a>
      )}
    </Template>
  );
}

export default Search;
