import React from "react";
import "./Search.scss";
import Template from "../components/Template";
import { useQuery } from "@apollo/client";
import { gql } from "../__generated__";

const LIST_CATS = gql(`
  query ListCats($after: String) {
    cats_connection(first: 10, after: $after) {
      edges {
        node {
          id
          name
          owner {
            id
            name
          }
        }
      }
      pageInfo {
        endCursor
        hasNextPage
      }
    }
  }
`);

function Search() {
  const handleSubmit = (event: React.FormEvent) => {
    event.preventDefault();
  };

  const { loading, error, data, fetchMore } = useQuery(LIST_CATS, {
    variables: { after: "" },
  });
  const handleMoreResults = async () => {
    const pageInfo = data?.cats_connection.pageInfo;
    if (!pageInfo?.hasNextPage) {
      console.log("No more!");
      return;
    }

    console.log("More!");
    await fetchMore({
      variables: { cursor: pageInfo?.endCursor },
    });
  };

  if (loading) return <Template>Loading...</Template>;
  if (error) return <Template>Error : {error.message}</Template>;

  // const elements = data?.cats?.map((c) => (
  //   <li key={c.id}>
  //     {c.name} - {c.owner.name}
  //   </li>
  // ));

  // const elements = data.

  return (
    <Template>
      <form onSubmit={handleSubmit}>
        <label>
          Search:
          <input type="text" name="name" />
        </label>
        <input type="submit" value="Submit" />
      </form>
      {/*<ol>{elements}</ol>*/}
      <a href="#" onClick={handleMoreResults}>
        More results
      </a>
    </Template>
  );
}

export default Search;
