import React from "react";
import "./MoreButton.scss";

type Props = {
  hasNext: boolean;
  loading: boolean;
  handleMoreResults: () => Promise<void>;
};

function MoreButton({ hasNext, loading, handleMoreResults }: Props) {
  return (
    <div className={"more-results-banner"}>
      <button onClick={handleMoreResults} hidden={!hasNext}>
        {" "}
        disabled={loading}
        More results
      </button>
    </div>
  );
}

export default MoreButton;
