import React from "react";
import "./MoreButton.scss";

type Props = {
  hasNext: boolean;
  fetchingMore: boolean;
  handleMoreResults: () => Promise<void>;
};

const buttonText = (fetchingMore: boolean) => {
  if (fetchingMore) {
    return "Fetching...";
  } else {
    return "More results";
  }
};

function MoreButton({ hasNext, fetchingMore, handleMoreResults }: Props) {
  return (
    <div className={"more-results-banner"}>
      <button
        onClick={handleMoreResults}
        hidden={!hasNext}
        disabled={fetchingMore}
      >
        {" "}
        {buttonText(fetch())}
      </button>
    </div>
  );
}

export default MoreButton;
