import React from "react";
import "./Search.scss";
import Template from "../components/Template";
import { useQuery } from "@apollo/client";
import { gql } from "../__generated__";

const LIST_CATS = gql(`
query ListCats(after: $cursor) {
  cats_connection(first: 10, after: $cursor) {
    edges {
      node {
        id
        name
        owner {
          id
          name
        }
      }
      cursor
    }
  }
}
`);

function Search() {
  const handleSubmit = (event: React.FormEvent) => {
    event.preventDefault();
  };

  const { loading, error, data, fetchMore } = useQuery(LIST_CATS, {
    variables: {
      after: "",
    },
  });
  const handleMoreResults = async () => {
    console.log("More!", data?.cats?.length);
    await fetchMore({
      variables: {
        offset: data?.cats?.length,
      },
    });
  };

  if (loading) return <Template>Loading...</Template>;
  if (error) return <Template>Error : {error.message}</Template>;

  const elements = data?.cats?.map((c) => (
    <li key={c.id}>
      {c.name} - {c.owner.name}
    </li>
  ));

  return (
    <Template>
      <form onSubmit={handleSubmit}>
        <label>
          Search:
          <input type="text" name="name" />
        </label>
        <input type="submit" value="Submit" />
      </form>
      <ol>{elements}</ol>
      <a href="#" onClick={handleMoreResults}>
        More results
      </a>
    </Template>
  );
}

export default Search;
