import React, { FormEventHandler, SyntheticEvent } from "react";
import "./Search.scss";
import Template from "../components/Template";
import { useQuery } from "@apollo/client";
import { gql } from "../__generated__";
import SearchBox from "../components/SearchBox";

const LIST_CATS = gql(`
  query ListCats($cursor: String, $search: String) {
    cats_connection(first: 5, after: $cursor, where: {name: {_ilike: $search}}) {
      edges {
        node {
          id
          name
          owner {
            name
          }
        }
      }
      pageInfo {
        endCursor
        hasNextPage
      }
    }
  }
`);

function Search() {
  const defaultSearchQuery = "%";

  const { loading, error, data, fetchMore, refetch } = useQuery(LIST_CATS, {
    variables: {
      search: defaultSearchQuery,
    },
  });

  const pageInfo = data?.cats_connection.pageInfo;
  const hasNext = pageInfo?.hasNextPage;
  const handleMoreResults = async () => {
    if (!hasNext) {
      return;
    }

    await fetchMore({
      variables: { cursor: pageInfo?.endCursor },
    });
  };

  if (loading) return <Template>Loading...</Template>;
  if (error) return <Template>Error : {error.message}</Template>;

  const catsList = data?.cats_connection.edges.map((e) => {
    const c = e.node;
    console.log(c.name, c.id);
    return (
      <li key={c.id}>
        {c.name} - {c.owner.name}
      </li>
    );
  });

  const moreButton = () => {
    if (hasNext) {
      return (
        <a href="#" onClick={handleMoreResults}>
          {" "}
          More results{" "}
        </a>
      );
    } else {
      return null;
    }
  };

  const handleChange = async (event: React.ChangeEvent<HTMLInputElement>) => {
    await refetch({
      search: event.target.value,
    });
  };

  return (
    <Template>
      <form>
        <label>
          Search:
          <input type="text" name="name" onInput={handleChange} />
        </label>
        <SearchBox onInput={async () => {}} />
      </form>
      <ol>{catsList}</ol>
      {moreButton()}
    </Template>
  );
}

export default Search;
