import "./Search.scss";
import Template from "../components/Template";
import { useQuery } from "@apollo/client";
import { gql } from "../__generated__";
import SearchBox from "../components/SearchBox";
import CatsList, { Cat } from "../components/CatsList";
import MoreButton from "../components/MoreButton";

const LIST_CATS = gql(`
  query ListCats($cursor: String, $search: String) {
    cats_connection(first: 5, after: $cursor, where: {name: {_iregex: $search}}, order_by: {id: asc}) {
      edges {
        node {
          id
          name
          owner {
            name
          }
        }
      }
      pageInfo {
        endCursor
        hasNextPage
      }
    }
  }
`);

function Search() {
  const defaultSearchQuery = ".*";

  const { loading, error, data, fetchMore, refetch } = useQuery(LIST_CATS, {
    variables: {
      search: defaultSearchQuery,
    },
  });
  if (loading) return <Template>Loading...</Template>;
  if (error) return <Template>Error : {error.message}</Template>;

  const pageInfo = data!.cats_connection.pageInfo;
  const hasNext = pageInfo!.hasNextPage;
  const handleMoreResults = async () => {
    if (!hasNext) {
      return;
    }

    await fetchMore({
      variables: { cursor: pageInfo.endCursor },
    });
  };

  const moreButton = () => {
    if (hasNext) {
      return (
        <a href="#" onClick={handleMoreResults}>
          {" "}
          More results{" "}
        </a>
      );
    } else {
      return null;
    }
  };

  const handleChange = async (input: string) => {
    await refetch({
      search: input,
    });
  };

  const cats = data!.cats_connection!.edges.map((edge) => edge.node) ?? [];

  return (
    <Template>
      <div className={"search-container"}>
        <div className={"label"}>Search:</div>
        <div className={"search-box"}>
          <SearchBox onInput={handleChange} />
        </div>
      </div>

      <CatsList cats={cats} />
      {moreButton()}
      <MoreButton hasNext={hasNext} handleMoreResults={handleMoreResults} />
    </Template>
  );
}

export default Search;
