import "./Search.scss";
import Template from "../components/Template";
import { useQuery } from "@apollo/client";
import { gql } from "../__generated__";
import SearchBox from "../components/SearchBox";
import CatsList, { Cat } from "../components/CatsList";
import MoreButton from "../components/MoreButton";
import { useLocation, useSearchParams } from "react-router-dom";
import { useEffect, useMemo } from "react";

const LIST_CATS = gql(`
  query ListCats($cursor: String, $search: String) {
    cats_connection(first: 5, after: $cursor, where: {name: {_iregex: $search}}, order_by: {id: asc}) {
      edges {
        node {
          id
          name
          owner {
            name
          }
        }
      }
      pageInfo {
        endCursor
        hasNextPage
      }
    }
  }
`);

const statusMessage = (message: string) => {
  return <Template>{message}</Template>;
};

const queryName = "q";

const useSearchQuery: () => [string, (query: string) => void] = () => {
  const [searchParams, setSearchParams] = useSearchParams();
  const query = searchParams.get(queryName) ?? ".*";

  const setSearchQuery = (query: string) => {
    const existingParams = Array.from(searchParams.entries());
    const newParams = new URLSearchParams([
      ...existingParams.filter(([key, _]) => key !== queryName),
      [queryName, query],
    ]);
    setSearchParams(newParams);
  };

  return [query, setSearchQuery];
};

function Search() {
  const [query, setQuery] = useSearchQuery();

  const { loading, error, data, fetchMore, refetch } = useQuery(LIST_CATS, {
    variables: {
      search: query,
    },
  });
  useEffect(() => {
    refetch({ search: query });
  }, [query]);

  const handleMoreResults = async () => {
    if (hasNext) {
      await fetchMore({
        variables: { cursor: pageInfo.endCursor },
      });
    }
  };

  const handleNewInput = async (input: string) => {
    // await refetch({ search: input });
    setQuery(input);
  };

  if (loading) return statusMessage("Loading...");
  if (error) return statusMessage("Error: " + error);

  if (data === undefined)
    return statusMessage("GraphQL endpoint did not return data.");

  const {
    cats_connection: {
      edges,
      pageInfo: { hasNextPage },
    },
  } = data;

  const cats = edges.map((edge) => edge.node);

  return (
    <Template>
      <div className={"search-container"}>
        <div className={"label"}>Search:</div>
        <div className={"search-box"}>
          <SearchBox onInput={handleNewInput} defaultQuery={query} />
        </div>
      </div>

      <CatsList cats={cats} />
      <MoreButton hasNext={hasNextPage} handleMoreResults={handleMoreResults} />
    </Template>
  );
}

export default Search;
